try {
    if (msg.payload && typeof msg.payload === 'string' && msg.payload.trim()) {
        const jsonData = JSON.parse(msg.payload.trim());
        if (jsonData && Array.isArray(jsonData) && jsonData.length > 0) {
            msg.payload = jsonData;
            return msg;
        }
    }
} catch (e) {
    node.warn("JSON Parse Error: " + e.message + " | Payload: '" + msg.payload + "'");
}
return null;

if (msg.payload && msg.payload.length > 0) {
    const data = msg.payload[0];

    // Calibration constants
    const X_OFFSET = 6200;
    const Y_OFFSET = 100;
    const Z_OFFSET = 15400;
    const SCALE = 16384;

    // Calculate G-forces
    const forwardG = (data.ax - X_OFFSET) / SCALE;
    const lateralG = (data.ay - Y_OFFSET) / SCALE;
    const verticalG = (data.az - Z_OFFSET) / SCALE;

    // Calculate lean angle in degrees
    const leanAngle = Math.asin(Math.max(-1, Math.min(1, lateralG))) * 57.3;

    // Enhanced GPS status detection
    const hasValidGPS = (data.latitude !== 0 || data.longitude !== 0) && data.gps_fix;
    const lat = hasValidGPS ? parseFloat(data.latitude) : 51.5074; // London default
    const lon = hasValidGPS ? parseFloat(data.longitude) : -0.1278;

    // Time since last update
    const lastUpdate = new Date(data.timestamp).toLocaleTimeString();

    // Create separate messages for each component
    const messages = [
        { topic: 'lean_angle', payload: parseFloat(leanAngle.toFixed(1)) },
        { topic: 'forward_g', payload: parseFloat(forwardG.toFixed(3)) },
        { topic: 'lateral_g', payload: parseFloat(lateralG.toFixed(3)) },
        { topic: 'speed', payload: parseFloat(data.speed_mph || 0) },
        { 
            topic: 'gps_map', 
            payload: {
                lat: lat,
                lon: lon,
                name: hasValidGPS ? 'Motorcycle Location' : 'No GPS Signal',
                icon: hasValidGPS ? 'motorcycle' : 'question-circle',
                iconColor: hasValidGPS ? 'blue' : 'red',
                popup: hasValidGPS ? `GPS Lock: ${lat.toFixed(6)}, ${lon.toFixed(6)}` : 'Waiting for GPS fix...'
            }
        },
        {
            topic: 'gps_status',
            payload: {
                hasGPS: hasValidGPS,
                latitude: data.latitude,
                longitude: data.longitude,
                gps_fix: data.gps_fix,
                speed: data.speed_mph || 0,
                lastUpdate: lastUpdate
            }
        },
        {
            topic: 'system_status',
            payload: {
                recordCount: context.get('counter') || 0,
                lastUpdate: lastUpdate,
                dataAge: Math.round((Date.now() - new Date(data.timestamp).getTime()) / 1000)
            }
        }
    ];

    // Increment counter
    const count = (context.get('counter') || 0) + 1;
    context.set('counter', count);
    messages[6].payload.recordCount = count;

    return messages;
}
return null;
